import React, { useState } from "react";

const Calender = ({ curDate }) => {
  const day = ["일", "월", "화", "수", "목", "금", "토"];

  const viewDate = {
    year: curDate.getFullYear(),
    date: curDate.getDate(),
    month: curDate.getMonth() + 1,
    day: curDate.toLocaleDateString(),
  };

  //0을 전달하면 입력한 달의 마지막 날짜를 가져옴
  const prevLast = new Date(viewDate.year, viewDate.month - 1, 0);
  const thisLast = new Date(viewDate.year, viewDate.month, 0);

  const PLDate = prevLast.getDate(); //지난달 마지막 날짜
  const PLDay = prevLast.getDay(); //지난달 마지막 요일

  const TLDate = thisLast.getDate();
  const TLDay = thisLast.getDay();

  const prevDates = [];
  const thisDates = [...Array(TLDate + 1).keys()].slice(1);
  const nextDates = [];

  // 달력에 표시될 이전 달 날짜
  if (PLDay !== 6) {
    for (let i = 0; i < PLDay + 1; i++) {
      prevDates.unshift(PLDate - i);
    }
  }

  // 달력에 표시 될 다음 달 날짜
  for (let i = 1; i < 7 - TLDay; i++) {
    nextDates.push(i);
  }

  const dates = prevDates.concat(thisDates, nextDates);

  // 이번달 1일의 인덱스 구하기
  const firstDateIndex = dates.indexOf(1);
  // 이번달 마지막날 인덱스 구하기
  const lastDateIndex = dates.lastIndexOf(TLDate);

  const today = new Date();
  const [findToday, setFindToday] = useState();
  if (
    viewDate.year === today.getFullYear &&
    viewDate.month === today.getMonth
  ) {
    findToday = dates.indexOf(today.getDate());
  }
  console.log(findToday);
  // 전달,다음달 날짜와 이번달 날짜 구분
  dates.map((it, idx) => {
    const condition =
      idx >= firstDateIndex && idx < lastDateIndex + 1 ? "this" : "other";

    const todayCheck = idx === findToday ? "today" : "";

    dates[idx] = (
      <div className="date">
        <span className={[condition, todayCheck]}>{it}</span>
      </div>
    );
  });

  const goToday = () => {};

  return (
    <div className="calender">
      <button className="btn" onClick={goToday}>
        오늘
      </button>
      <div className="days">
        {day.map((it) => (
          <div className="day">{it}</div>
        ))}
      </div>
      <div className="dates">
        {dates.map((it) => (
          <>{it}</>
        ))}
      </div>
    </div>
  );
};

export default Calender;
